mapper.select:
SELECT COUNT(*)
  FROM follows
 WHERE follower_id = #{followerId}
   AND following_id = #{followingId}

SELECT COUNT(*)
  FROM follows
 WHERE follower_id = #{followerId}
   AND following_id = #{followingId}
   AND is_active = 1

mapper.update:
UPDATE follows
   SET is_active = 1
     , updated_at = DATETIME('now')
 WHERE follower_id = #{followerId}
   AND following_id = #{followingId}
   AND is_active = 0

UPDATE follows
   SET is_active = 0
     , updated_at = DATETIME('now')
 WHERE follower_id = #{followerId}
   AND following_id = #{followingId}
   AND is_active = 1
